import { Component, OnInit }          from '@angular/core';
import { ShortenLinkCommand }         from 'src/app/Commands/ShortenLinkCommand';
import { ShortenLinkCommandResult }   from './../../Commands/ShortenLinkCommand';
import { LinkService as LinkService } from 'src/app/Services/Link.service';

@Component({
  selector   : 'app-shorten',
  templateUrl: './shorten.component.html',
  styleUrls  : ['./shorten.component.css']
})
export class ShortenComponent implements OnInit {

  public shortenUrlCommand: ShortenLinkCommand             = new ShortenLinkCommand("");
  public shortenUrlCommandResult: ShortenLinkCommandResult = new ShortenLinkCommandResult(0, "", "", new Date(), new Date(), true);

  constructor(private linkService: LinkService) { }

  ngOnInit() {
  }



  public clearUrlBox() {
    this.shortenUrlCommand.originalUrl = "";
  }


  public shorten(url: string) {
    this.linkService.shorten(url).subscribe(result => {
      this.shortenUrlCommandResult = result;
    });
  }


  public copyTheShortUrlToClipboard() {

    const element = document.getElementById("new-url-label") as HTMLDivElement;

    if (element) {
      const innerText = element.innerText;

      // Remove the "Your short URL: " part
      const textToCopy = innerText.substring(17);
      navigator.clipboard.writeText(textToCopy);
    }
  }


}
<body>
  <main>
    <div id="header-div" class="">
      <h1 class="">Link Shortener</h1>
    </div>
    <!-- link input and shorten button -->
    <div id="content-div">
      <div id="input-div">
        <input
          type="text"
          class="text-field content-row"
          placeholder="Enter URL here . . ."
          id="input-field"
          required
          [(ngModel)]="shortenUrlCommand.originalUrl"
        />
        <button id="shorten" type="button" class="content-row button" (click)="shorten(shortenUrlCommand.originalUrl)">
          Shorten URL
        </button>
        <button type="button" id="clear-btn" class="content-row button" (click)="clearUrlBox()">
          Clear
        </button>
      </div>
      <!-- Output and copy -->
      <div id="output-div">
        <div class="content-row" id="new-url-label">Your short URL: {{shortenUrlCommandResult.shortUrl}}</div>
        <div id="new-url" class="content-row"></div>
        <button
          type="button"
          id="copy-btn"
          data-clipboard-target="#new-url"
          class="content-row button"
          (click)="copyTheShortUrlToClipboard()"
        >
          Copy
        </button>
      </div>
      <div class="" id="error-div">
        <p class="content-row" id="error-text"></p>
      </div>
    </div>
  </main>






  <!--App script-->
  <script>
    const token = "503bf5006708dc769617c571f07206858ef8c018";
    const button = document.querySelector("#shorten");
    const input = document.querySelector("#input-field");
    const longUrl = document.querySelector("#input-url");
    const shortUrl = document.querySelector("#new-url");
    const resultDiv = document.querySelector("#output-div");
    const errorDiv = document.querySelector("#error-div");
    const errorMessage = document.querySelector("#error-text");
    const clearButton = document.querySelector("#clear-btn");
    const copyButton = document.querySelector("#copy-btn");

    /* button action */
    button.addEventListener("click", (event) => {
      event.preventDefault();
      if (input.value) {
        shorten(input.value);
      } else {
        showError();
        hideResult();
      }
    });

    /* function to handle errors */
    const handleError = (response) => {
      console.log(response);
      if (!response.ok) {
        errorMessage.textContent = "Please enter a valid URL.";
        showError();
        hideResult();
      } else {
        hideError();
        return response;
      }
    };

    /* function to get shortened url with input "url" with fetch and deal with error */
    const shorten = (input) => {
      fetch("https://api-ssl.bitly.com/v4/shorten", {
        method: "POST",
        headers: {
          Authorization: `Bearer ${token}`,
          "Content-Type": "application/json",
        },
        body: JSON.stringify({ long_url: input, domain: "bit.ly" }),
      })
        .then(handleError)
        .then((response) => response.json())
        .then((json) => {
          shortUrl.innerHTML = json.link;
          showResult();
        })
        .catch((error) => {
          console.log(error);
        });
    };

    /* Clipboard functions */

    const clipboard = new ClipboardJS("#copy-btn");

    clipboard.on("success", function (e) {
      console.info("Action:", e.action);
      console.info("Text:", e.text);
      console.info("Trigger:", e.trigger);

      e.clearSelection();
    });

    clipboard.on("error", function (e) {
      console.error("Action:", e.action);
      console.error("Trigger:", e.trigger);
    });

    /* Clear fields */
    const clearFields = () => {
      input.value = "";
      hideResult();
      hideError();
    };

    clearButton.addEventListener("click", (event) => {
      event.preventDefault();
      clearFields();
    });

    /* display/hide results and errors */
    const showResult = () => {
      shortUrl.style.display = "flex";
    };

    const hideResult = () => {
      shortUrl.style.display = "none";
    };

    const showError = () => {
      errorDiv.style.display = "block";
    };

    const hideError = () => {
      errorDiv.style.display = "none";
    };
  </script>
</body>
